#include &lt;bits/stdc++.h&gt;using namespace std; using testimony = pair&lt;int, int&gt;; // first: 人, second: 正直者か不親切か  int N; vector&lt;vector&lt;testimony&gt;&gt; testimonies; bool check(int bit) { // N人それぞれについて  for (int i = 0; i &lt; N; i++) { // その人が「不親切な人」ならその人の証言は調べなくて良い  if (!((bit &gt;&gt; i) &amp; 1)) continue; // 各証言の整合性を取る  for (auto test : testimonies[i]) { int x = test.first; int y = test.second; if (y == 0 &amp;&amp; ((bit &gt;&gt; x) &amp; 1)) return false; if (y == 1 &amp;&amp; !((bit &gt;&gt; x) &amp; 1)) return false; } } return true; } int main() { cin &gt;&gt; N; testimonies.resize(N); for (int i = 0; i &lt; N; i++) { int A; cin &gt;&gt; A; testimonies[i].resize(A); for (int j = 0; j &lt; A; j++) { cin &gt;&gt; testimonies[i][j].first &gt;&gt; testimonies[i][j].second; testimonies[i][j].first -= 1; } } int res = 0; // 全通りを調べ上げる  for (int bit = 0; bit &lt; (1 &lt;&lt; N); bit++) { // 決め打ちした「正直者-不親切な人分布」与えられた証言と整合性があるか  if (check(bit)) { int cnt = 0; // 正直者をカウント  for (int i = 0; i &lt; N; i++) { if ((bit &gt;&gt; i) &amp; 1) { cnt++; } } res = max(res, cnt); } } cout &lt;&lt; res &lt;&lt; endl; return 0; } ABC 2 D $N$人いる国会議員について，最大派閥に所属するか否かの場合の数の総数は$2^N$通りであり，$1 \leq N \leq 12$という$N$の制約を考えると全パターンを調べ上げることで原理的に解ける．
#include &lt;bits/stdc++.h&gt;using namespace std; int main() { int n, m; cin &gt;&gt; n &gt;&gt; m; vector&lt;vector&lt;bool&gt;&gt; friends(n, vector&lt;bool&gt;(n, false)); for (int i = 0; i &lt; m; i++) { int x, y; cin &gt;&gt; x &gt;&gt; y; x--; y--; friends[x][y] = friends[y][x] = true; } int ans = 0; for (int bit = 0; bit &lt; (1 &lt;&lt; n); bit++) { bool flag = true; for (int i = 0; i &lt; n; i++) { if ((bit &gt;&gt; i) &amp; 1) { for (int j = 0; j &lt; i; j++) { if ((bit &gt;&gt; j) &amp; 1) { if (!friends[i][j]) flag = false; } } } } if (flag) ans = max(ans, __builtin_popcount(bit)); } cout &lt;&lt; ans &lt;&lt; endl; return 0; } JOI2008 予選5 行のひっくり返し方を固定したとき，ある列をひっくり返すか否かは他の列をひっくり返すか否かの判断に影響を与えない．よって，各列において出荷可能なせんべいの枚数を最大化するしたとき，全体として出荷できるせんべいの枚数を（その行のひっくり返し方において）最大化できるといえる．各列のひっくり返すか中の判断は，「列を上から舐めていき，表になっているせんべいの枚数のほうが裏になっているせんべいより多ければひっくり返す．そうでなければひっくり返さない」とすればその列で焼いているせんべいのうち，出荷可能なせんべいの枚数を最大にすることができる．
まとめると，行のひっくり返し方（総数$2^C$通り）のそれぞれに対して，各列の状態を見ていき，「表になっているせんべいの枚数のほうが裏になっているせんべいより多ければひっくり返す．そうでなければひっくり返さない」という操作を行って，全体として出荷できるせんべいの枚数を調べ，その最大値を計算する．
#include &lt;bits/stdc++.h&gt;using namespace std; int table[10][10000]; int main() { int row, col; cin &gt;&gt; row &gt;&gt; col; for (int i = 0; i &lt; row; i++) { for (int j = 0; j &lt; col; j++) { cin &gt;&gt; table[i][j]; } } int ans = 0; for (int bit = 0; bit &lt; (1 &lt;&lt; row); bit++) { int cnt = 0; for (int j = 0; j &lt; col; j++) { int omote = 0, ura = 0, state; for (int r = 0; r &lt; row; r++) { state = table[r][j]; if ((bit &gt;&gt; r) &amp; 1) { state = !state; } if (state) omote++; else ura++; } cnt += max(omote, ura); } ans = max(ans, cnt); } cout &lt;&lt; ans &lt;&lt; endl; return 0; } Square869120Contest #4 B $N$個ある建物について，どの建物が見えて，どの建物が見えないかはぜんぶで$2^N$通りであるが，$K$色見えてほしいので，$N$桁のビット列から$K$桁だけ1が立っているようなビット列を計算できたら，ビット全探索と同様に考えることができる．建物の見え方を固定したとき，建物を左から順番に見ていって，見えてほしい建物はその建物より左側にある最も背の高い建物より1だけ高く，見えなくてもいい建物はそのままにしておく．
#include &lt;bits/stdc++.h&gt;using namespace std; int next_combination(int bit) { int x = bit &amp; -bit; int y = bit + x; return (((bit &amp; ~y) / x) &gt;&gt; 1) | y; } int main() { int n, k; cin &gt;&gt; n &gt;&gt; k; k--; vector&lt;long long&gt; a(n); for (int i = 0; i &lt; n; i++) cin &gt;&gt; a[i]; if (k == 0) { cout &lt;&lt; 0 &lt;&lt; endl; return 0; } n--; long long res = 1001001001001; int bit = (1 &lt;&lt; k) - 1; for (; bit &lt; (1 &lt;&lt; n); bit = next_combination(bit)) { long long highest = a[0]; long long tmp = 0; for (int i = 0; i &lt; n; i++) { if ((bit &gt;&gt; i) &amp; 1) { if (a[i+1] &lt;= highest) { tmp += (highest - a[i+1] + 1); highest += 1; } else { highest = a[i+1]; } } else { if (highest &lt; a[i+1]) { highest = a[i+1]; } } } res = min(res, tmp); } cout &lt;&lt; res &lt;&lt;endl; return 0; } 順列全探索 ABC 145 C 順列全探索を使えっていう意図が丸裸って感じの問題．
#include &lt;bits/stdc++.h&gt;using namespace std; int N; double x[10], y[10]; double dist(int i, int j) { double dx = x[i] - x[j]; double dy = y[i] - y[j]; return pow(dx*dx + dy*dy, 0.5); } int main() { cin &gt;&gt; N; for (int i = 1; i &lt;= N; i++) cin &gt;&gt; x[i] &gt;&gt; y[i]; double sum = 0.0; vector&lt;int&gt; towns(N); for (int i = 0; i &lt; N; i++) towns[i] = i+1; do { for (int i = 0; i &lt; N-1; i++) { sum += dist(towns[i], towns[i+1]); } } while (next_permutation(towns.begin(), towns.end())); int fact = 1; for (int i = 2; i &lt;= N; i++) fact *= i; cout &lt;&lt; fixed &lt;&lt; setprecision(12) &lt;&lt; sum/fact &lt;&lt; endl; return 0; } ABC 150 C do {...} while (next_permutation());の練習問題．ソートし直すのを忘れないように．
#include &lt;bits/stdc++.h&gt;using namespace std; int main() { int n; cin &gt;&gt; n; vector&lt;int&gt; p(n), q(n); for (int i = 0; i &lt; n; i++) cin &gt;&gt; p[i]; for (int i = 0; i &lt; n; i++) cin &gt;&gt; q[i]; vector&lt;int&gt; s(n); for (int i = 0; i &lt; n; i++) s[i] = i+1; int i = 0; int pidx = 0; int qidx = 0; do { bool pflag = true; for (int i = 0; i &lt; n; i++) { if (s[i] != p[i]) pflag = false; } if (pflag) { pidx = i; break; } else i++; } while (next_permutation(s.begin(), s.end())); sort(s.begin(), s.end()); i = 0; do { bool qflag = true; for (int i = 0; i &lt; n; i++) { if (s[i] != q[i]) qflag = false; } if (qflag) { qidx = i; break; } else i++; } while (next_permutation(s.begin(), s.end())); cout &lt;&lt; abs(pidx - qidx) &lt;&lt; endl; return 0; } ABC 54 C 1を始点とするDFSで条件を満たすパスを数え上げる方法が一番素直だけれども，制約に注目すると，与えられるグラフの頂点数が最大でも8ということを考慮すると，頂点の訪問順序を，生えている辺を無視して順列全探索をして，編の生え方に矛盾しないものをカウントするとしても解ける．まあ教育的な観点からはDFSの練習として思った方が良さそうだけれど．
 DFSによる解答  #include &lt;bits/stdc++.h&gt;using namespace std; // 1を始点としてDFSでグラフ上を探索し条件を満たすパスを数え上げる  const int nmax = 8; bool graph[nmax][nmax]; int dfs(int v, int N, bool has_visited[nmax]) { bool has_visited_all = true; for (int i = 0; i &lt; N; i++) { if (has_visited[i] == false) { has_visited_all = false; } } if (has_visited_all) { return 1; } int ret = 0; for (int i = 0; i &lt; N; i++) { if (graph[v][i] == false) continue; if (has_visited[i]) continue; has_visited[i] = true; ret += dfs(i, N, has_visited); has_visited[i] = false; } return ret; } int main() { int N, M; cin &gt;&gt; N &gt;&gt; M; for (int i = 0; i &lt; M; i++) { int A, B; cin &gt;&gt; A &gt;&gt; B; graph[A-1][B-1] = graph[B-1][A-1] = true; } bool has_visited[nmax]; for (int i = 0; i &lt; N; i++) { has_visited[i] = false; } has_visited[0] = true; cout &lt;&lt; dfs(0, N, has_visited) &lt;&lt; endl; return 0; }  順列全探索による解答  #include &lt;bits/stdc++.h&gt;using namespace std; const int nmax = 8; bool graph[nmax][nmax]; int main() { int N, M; cin &gt;&gt; N &gt;&gt; M; for (int i = 0; i &lt; M; i++) { int A, B; cin &gt;&gt; A &gt;&gt; B; graph[A-1][B-1] = graph[B-1][A-1] = true; } vector&lt;int&gt; nodes(N); for (int i = 0; i &lt; N; i++) nodes[i] = i; int cnt = 0; do { bool flag = true; if (nodes[0] != 0) continue; for (int i = 0; i &lt; N-1; i++) { if (graph[nodes[i]][nodes[i+1]] == false) flag = false; continue; } if (flag) cnt++; } while (next_permutation(nodes.begin(), nodes.end())); cout &lt;&lt; cnt &lt;&lt; endl; return 0; } おわりに 😄 これでぼくも全探索マスター！

情報セキュリティの構成要素 「情報セキュリティ」の言葉の指し示す意味範囲はOECDの情報セキュリティガイドラインやISO/IEC TR133351として国際的に定義されている．
ISO/IEC TR13335にて情報セキュリティとは下記6要素のことを指すとされている．
 機密性 Confidentiality 完全性 Integrity 可用性 Availability 責任追跡性 Accountability 真正性 Authenticity 信頼性 Reliability  機密性 Confidentiality  意味  意図した相手以外に情報が漏れないこと   リスク  盗聴や内部からの情報漏洩   対策  暗号技術    完全性 Integrity  意味  情報が正確であること   リスク  情報の改ざん，ノイズによるビット反転・ビットの欠落   対策  誤り訂正符号，ハッシュ関数，メッセージ認証コード，デジタル署名    可用性 Availability  意味  ある情報にアクセスすることが許されている主体が，任意の時点で情報にアクセスすることができること   リスク  システムへの過負荷，災害，意図しないロック   対策  システムの多重化，クラウド化，負荷分散    責任追跡性 Accountability  意味  ユーザやシステムの振る舞いについて説明が可能であること   リスク  ログの改ざん，否認   対策  ロギング，デジタル署名（否認防止）    真正性 Authenticity  意味  観測されるユーザやシステムの振る舞いが，その主体によるものであること（なりすましではない）   リスク  なりすまし   対策  認証，デジタル署名（なりすまし防止）    信頼性 Reliability  意味  システムが一貫して動作すること   リスク  盗聴や内部からの情報漏洩   対策  システムの多重化，負荷の監視      正確には企業のセキュリティリスクを査定する際のガイドラインを定めたものになっている．通称GMITS（Guidelines for the Management for IT Security） &#x21a9;&#xfe0e;
